INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES ( 'Sulphur Mountain Cosmic Ray Station', 'Rather than taking the gondola up Sulphur Mountain, you can hike the trail up to the top of the ridge and catch the gondola down. (It''s half price to just take the gondola down, but it''s free before 10AM and after 7PM.) The trail is steep but manageable for those with a reasonable level of fitness.', 'cultural', 'https://upload.wikimedia.org/wikipedia/commons/3/34/Sulphur_Mountain_Cosmic_Ray_Station_National_Histo_%28206260683%29.jpeg', 'Northern Lights Cafe, Sulphur Moutain Trail, Improvement District No. 9, Alberta, T1L, Canada', '(51.1444594000000023,-115.574970300000004)', true);
