INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES ( 'Golden Gate Bridge', 'The Frommer''s travel guide describes the Golden Gate Bridge as "possibly the most beautiful, certainly the most photographed, bridge in the world." At the time of its opening in 1937, it was both the longest and the tallest suspension bridge in the world, with a main span of 4,200ft and a total height of 746ft.', 'landmark', 'https://farm1.staticflickr.com/135/336886787_c3b1ce5f4a_o.jpg', 'Golden Gate Bridge, Battery East Road, San Francisco, San Francisco City and County, California, 94129, United States', '(37.8202404009198716,-122.478573008458554)', true);
INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES ( 'Alcatraz', 'The Alcatraz Federal Penitentiary or United States Penitentiary, Alcatraz Island (often referred to as Alcatraz [/ˈælkəˌtræz/, (Latin America)/ (Spain) from Arabic. غطاس] or The Rock) was a maximum security federal prison on Alcatraz Island, 1.25mi off the coast of San Francisco, California, United States, the site of a fort since the 1850s; the main prison building was built in 1910–1912 as a United States Army military prison.', 'cultural', 'https://upload.wikimedia.org/wikipedia/commons/0/0a/Alcatraz_Island_photo_D_Ramey_Logan.jpg', 'Alcatraz Island, Parade Ground, San Francisco, San Francisco City and County, California, United States', '(37.8266308999999978,-122.422855200000001)', true);
