INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES (274, 'Edo-Tokyo Museum', 'One of the best museums in Tokyo, and that''s saying something, this bizarre multi-story edifice suspended in midair and bearing not a small resemblance to a Star Wars Imperial Walker (meant to be a replica of an old raised warehouse) covers the history of the metropolis, starting from 1590 when it was selected as Japan''s new capital Edo, all the way through the Kanto earthquake and firebombings of World War II.', 'cultural', 'https://upload.wikimedia.org/wikipedia/commons/7/73/Edo-Tokyo_Museum.jpg', '1-4-1 Yokoami', '(35.6964430549221419,139.796076174670418)', true);
