INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES (378, 'Kyoto National Museum', 'It is near Sanjusangen-do, and has a large collection of ancient Japanese sculpture, ceramics, metalwork, painting, and other artifacts. (It''s quite similar to the Tokyo National Museum in Tokyo/Ueno.) The Museum building is fairly grand, but the statue of Rodin''s The Thinker out front is a bit out of place, as there''s no Western art inside.', 'cultural', 'https://upload.wikimedia.org/wikipedia/commons/b/bb/Kyoto_National_Museum_2009.jpg', '527 Chayamachi, Higashiyama-ku', '(34.9900453434032386,135.772872085435949)', true);
