INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES (574, 'Piazza dei Miracoli', 'Cathedral Square), is a walled 8.87-hectare area located in Pisa, Tuscany, Italy, recognized as an important centre of European medieval art and one of the finest architectural complexes in the world.', 'landmark', 'https://farm7.staticflickr.com/6142/5977219439_5b976f7237_o.jpg', 'Camposanto Monumentale, Piazza del Duomo, Santa Maria, Pisa, Toscana, 56126, Italia', '(43.723621449999996,10.3951273499999992)', true);
