INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES (737, 'Wanda Metropolitano', 'Metropolitano Stadium (Spanish. Estadio Metropolitano), also referred to as Wanda Metropolitano for sponsorship reasons, is a stadium in Madrid, Spain.', 'landmark', 'https://upload.wikimedia.org/wikipedia/commons/0/05/Wanda-Metropolitano.jpg', 'Wanda Metropolitano, Avenida 26 de Abril, Rosas, San Blas - Canillejas, Área metropolitana de Madrid y Corredor del Henares, Comunidad de Madrid, 28001, España', '(40.4361742987702399,-3.59989578435886148)', true);
INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES (738, 'Estadio El Soto', 'Estadio El Soto is a stadium in Móstoles, Madrid, Spain. It is currently used for football matches and is the home stadium of CD Móstoles URJC.', 'landmark', NULL, 'Campo de fútbol El Soto, Autovía del Suroeste, Móstoles, Área metropolitana de Madrid y Corredor del Henares, Comunidad de Madrid, 28935, España', '(40.3258215000000035,-3.89427579999999995)', true);
