INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('Acropolis', 'The Acropolis of Athens is an ancient citadel located on a rocky outcrop above the city of Athens and contains the remains of several ancient buildings of great architectural and historic significance, the most famous being the Parthenon.', 'landmark', 'https://farm7.staticflickr.com/6134/5998106568_580e4c128d_o.jpg', 'Ακρόπολη, Περίπατος Ακρόπολης (Νότια κλιτύς), Αναφιώτικα, 3η Κοινότητα Αθηνών, Αθήνα, Δήμος Αθηναίων, Περιφερειακή Ενότητα Κεντρικού Τομέα Αθηνών, Περιφέρεια Αττικής, Αποκεντρωμένη Διοίκηση Αττικής, 10558, Ελλάς', '(37.9716987999999986,23.7264826000000006)', true);
