INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('Tomb of the Unknown Soldier', 'The Tomb of the Unknown Soldier (Μνημείο του Αγνώστου Στρατιώτη) is a war memorial located in Syntagma Square in Athens, in front of the Old Royal Palace.', 'landmark', 'https://upload.wikimedia.org/wikipedia/commons/f/f0/Tomb_of_Unknown_at_Syntagma_Square.jpg', 'Μνημείο Αγνώστου Στρατιώτου, Πλατεία Αγνώστου Στρατιώτου, Κολωνάκι, 1η Κοινότητα Αθηνών, Αθήνα, Δήμος Αθηναίων, Περιφερειακή Ενότητα Κεντρικού Τομέα Αθηνών, Περιφέρεια Αττικής, Αποκεντρωμένη Διοίκηση Αττικής, 105 64, Ελλάς', '(37.9753684000000007,23.7363725000000017)', true);
INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('Odeon of Herodes Atticus', 'This theatre, built in 161 CE, is still used for concerts and plays.', 'landmark', 'https://farm3.staticflickr.com/2205/2362462544_82c9b1353c_o.jpg', 'Ωδείο του Ηρώδου του Αττικού, Περίπατος Ακρόπολης (Νότια κλιτύς), Αναφιώτικα, 3η Κοινότητα Αθηνών, Αθήνα, Δήμος Αθηναίων, Περιφερειακή Ενότητα Κεντρικού Τομέα Αθηνών, Περιφέρεια Αττικής, Αποκεντρωμένη Διοίκηση Αττικής, 10558, Ελλάς', '(37.9708428884788987,23.724594983961012)', true);
INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('Borgia Apartments', 'The paintings and frescoes, which were executed between 1492 and 1494, drew on a complex iconographic program that used themes from medieval encyclopedias, adding an eschatological layer of meaning and celebrating the supposedly divine origins of the Borgias.', 'landmark', 'https://upload.wikimedia.org/wikipedia/commons/e/e9/Borgia_Apartment_002.jpeg', 'no address found', '(41.9026862999999992,12.4558862999999995)', true);
INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('Mamertine Prison', 'Romans and leaders of Rome''s defeated enemies were imprisoned here where they either died of starvation or strangulation.', 'cultural', 'https://upload.wikimedia.org/wikipedia/commons/8/8f/Mamertine_Prison.jpg', 'no address found', '(41.8931419443791313,12.4845856493439022)', true);
INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('St. Peter''s Square', 'Most of the Piazza di San Pietro is actually an oval.', 'landmark', 'https://upload.wikimedia.org/wikipedia/commons/5/59/2004_Saint_Peter%27s_Square.jpg', 'no address found', '(41.9018541000000013,12.4557602999999997)', true);
INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('Capitoline Hill', 'On top of the Capitoline hill. The piazza was designed by Michelangelo. The Capitoline Museum is housed in the palaces flanking the piazza.', 'landmark', 'https://farm4.staticflickr.com/3490/3912843315_07886b2a69_o.jpg', 'no address found', '(41.8929482109527882,12.4835876449987406)', true);