INSERT INTO attractions (name, description, category, image, address, location, visible) VALUES ('Tomb of the Unknown Soldier', 'The Tomb of the Unknown Soldier (Μνημείο του Αγνώστου Στρατιώτη) is a war memorial located in Syntagma Square in Athens, in front of the Old Royal Palace.', 'landmark', 'https://upload.wikimedia.org/wikipedia/commons/f/f0/Tomb_of_Unknown_at_Syntagma_Square.jpg', 'Μνημείο Αγνώστου Στρατιώτου, Πλατεία Αγνώστου Στρατιώτου, Κολωνάκι, 1η Κοινότητα Αθηνών, Αθήνα, Δήμος Αθηναίων, Περιφερειακή Ενότητα Κεντρικού Τομέα Αθηνών, Περιφέρεια Αττικής, Αποκεντρωμένη Διοίκηση Αττικής, 105 64, Ελλάς', '(37.9753684000000007,23.7363725000000017)', true);
