INSERT INTO attractions ( name, description, category, image, address, location, visible) VALUES ( 'Marina Bay Sands', 'The Sands'' shopping mall, chock full of expensive brands geared at stripping jackpot winners of their earnings, plus a dinky little canal where you can go for a spin in a sampan boat at $10/person.', 'adult', 'https://upload.wikimedia.org/wikipedia/commons/f/f9/Marina_Bay_Sands_in_the_evening_-_20101120.jpg', '10 Bayfront Avenue', '(1.28402379999999994,103.859641999999994)', true);
