INSERT INTO attractions (id, name, description, category, image, address, location, visible) VALUES (380, 'Kyoto Imperial Palace', 'The palace is a reconstruction (dating from 1855), though the Emperor doesn''t spend much time there, and the guided tour doesn''t enter the palace buildings, only peeking at them from the outside, but nevertheless, it provides interesting insight into the lives of the Imperial Court and it''s the only Imperial site in Kyoto that offers English guides.', 'landmark', 'https://upload.wikimedia.org/wikipedia/commons/7/74/Imperial_Palace_Kyoto_model.jpg', '京都府京都市上京区京都御苑3', '(35.0231604000000019,135.761449199999987)', true);
