INSERT INTO attractions (id, name, description, category, image, address, location, visible) VALUES (578, 'Santa Maria della Spina', 'A very small Gothic church built in 1230 to house a thorn from Jesus''s crown, it''s considered one of the best expressions of Italian Gothic.', 'cultural', 'https://farm6.staticflickr.com/5174/5390481369_b4a39136dc_b.jpg', 'Chiesa di Santa Maria della Spina, Lungarno Gambacorti, Sant''Antonio, Pisa, Toscana, 56125, Italia', '(43.7152436832861397,10.3967304198773789)', true);
